$headers = $message->getHeaders();

//Get the To: header
$toHeader = $headers->get('To');

//Get all headers named "X-Foo"
$fooHeaders = $headers->getAll('X-Foo');

//Get the second header named "X-Foo"
$foo = $headers->get('X-Foo', 1);

//Get all headers that are present
$all = $headers->getAll();
